import './App.css';
import React,{ useEffect, useState } from 'react';
import Movie from './components/Movie'

function App() {
  const [movieTitle,setMovieTitle]= useState("");
  const [movieYear,setMovieYear]= useState("");
  const movieList =[
    { title: "Heath Chang 1",
      year: 2001  
    },
    { title: "Heath Chang 2",
      year: 2002  
    },
    { title: "Heath Chang 3",
      year: 2003 
    }
  ]
  const renderMovies = movieList.map(movie=>{
    return(
      <Movie movie={movie} key={movie.title}/>
    )
  });
  return (
    <div className="App">
      <h1>Heath Chang</h1>
      <form>
        <input
          type="text"
          value={movieTitle}
          placeholder="영화제목"
          onChange={e=>{setMovieTitle("e.target.value")}}
        />
      </form>  
      <form>
        <input
          type="text"
          value={movieYear}
          placeholder="개봉년도"
          onChange={e=>{setMovieYear("e.target.value")}}
        />
      </form>
      <button> 영화 추가</button>  
      {renderMovies}
    </div>

  );
}

export default App;
