import './App.css';

function App() {

  const onSubmit=(text)=>{
    alert("submitted");
  }
  const onKeyup=(event)=>{
    if(event.keyCode===13){
      onSubmit();
    }
  }
  let text ="Hello1";
  const updateText= ()=>{
    text = 'Coder';
    console.log(text);
    //document.getElementById("text").innerHTML=text;
  }

  return (
    <div className="App">
      <input onKeyUp={onKeyup}/>
      <button onClick={()=>{
        onSubmit();
      }}>Submit</button>
      <br/> <br/>
      <span /*id="text"*/> {text} </span>
      <button onClick={updateText}> Update </button>
    </div>
  );
}

export default App;
